import asyncio

import aiohttp


async def test_txqq(keyword: str):
    url = "https://music.txqq.pro/"
    data = {
        "input": keyword,
        "filter": "name",
        "type": "netease",
        "page": 1,
    }

    headers = {
        "User-Agent": (
            "Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:146.0) "
            "Gecko/20100101 Firefox/146.0"
        ),
        "Accept": "application/json, text/javascript, */*; q=0.01",
        "Content-Type": "application/x-www-form-urlencoded; charset=UTF-8",
        "X-Requested-With": "XMLHttpRequest",
        "Origin": "https://music.txqq.pro",
        "Referer": f"https://music.txqq.pro/?name={keyword}&type=netease",
    }

    async with aiohttp.ClientSession() as session:
        try:
            async with session.post(url, data=data, headers=headers) as resp:
                print(f"状态码: {resp.status}")
                text = await resp.text()
                print("前1000字符预览:", text[:1000])

                # 尝试解析 JSON，不依赖 Content-Type
                try:
                    result = await resp.json(content_type=None)
                    print("JSON 结果:", result)
                except Exception as e:
                    print("JSON 解析失败:", e)

        except Exception as e:
            print("请求失败:", e)


if __name__ == "__main__":
    asyncio.run(test_txqq("不要说话 陈奕迅"))


